// Input.tsx
import React, { useState } from "react";

type Point = {
  x: number;
  y: number;
};

interface InputProps {
  setPoints: (points: Point[]) => void;
}

const Input: React.FC<InputProps> = ({ setPoints }) => {
  const [point0, setPoint0] = useState<Point>({ x: 0, y: 0 });
  const [point1, setPoint1] = useState<Point>({ x: 0, y: 0 });
  const [point2, setPoint2] = useState<Point>({ x: 0, y: 0 });

  const handlePointChange = (
    event: React.ChangeEvent<HTMLInputElement>,
    pointSetter: React.Dispatch<React.SetStateAction<Point>>
  ) => {
    const { name, value } = event.target;
    pointSetter((prev) => ({ ...prev, [name]: parseFloat(value) }));
  };

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    setPoints([point0, point1, point2]);
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files ? event.target.files[0] : null;
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const text = e.target?.result;
        try {
          const points = JSON.parse(text as string);
          setPoints(points);
        } catch (error) {
          console.error("Error parsing JSON", error);
        }
      };
      reader.readAsText(file);
    }
  };

  [point0, point1, point2];
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Point 0 X:
          <input
            type="number"
            name="x"
            value={point0.x}
            onChange={(e) => handlePointChange(e, setPoint0)}
          />
        </label>
        <label>
          Point 0 Y:
          <input
            type="number"
            name="y"
            value={point0.y}
            onChange={(e) => handlePointChange(e, setPoint0)}
          />
        </label>
        <label>
          Point 1 X:
          <input
            type="number"
            name="x"
            value={point1.x}
            onChange={(e) => handlePointChange(e, setPoint1)}
          />
        </label>
        <label>
          Point 1 Y:
          <input
            type="number"
            name="y"
            value={point1.y}
            onChange={(e) => handlePointChange(e, setPoint1)}
          />
        </label>
        <label>
          Point 2 X:
          <input
            type="number"
            name="x"
            value={point2.x}
            onChange={(e) => handlePointChange(e, setPoint2)}
          />
        </label>
        <label>
          Point 2 Y:
          <input
            type="number"
            name="y"
            value={point2.y}
            onChange={(e) => handlePointChange(e, setPoint2)}
          />
        </label>
        <button type="submit">Set Points</button>
      </form>
      <input type="file" onChange={handleFileChange} accept=".json" />
    </div>
  );
};

export default Input;
