// CanvasComponent.tsx
import React, { useRef, useEffect } from "react";

type Point = {
  x: number;
  y: number;
};

interface CanvasProps {
  points: Point[];
  bezierPoints: Point[];
}

const CanvasComponent: React.FC<CanvasProps> = ({ points, bezierPoints }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const drawGrid = (
    context: CanvasRenderingContext2D,
    width: number,
    height: number
  ) => {
    const gridSize = Math.min(width / 10, height / 10); // Define the size of the grid
    context.strokeStyle = "#e9e9e9"; // Light grey color for the grid lines

    // Draw vertical grid lines
    for (let x = 0; x <= width; x += gridSize) {
      context.beginPath();
      context.moveTo(x, 0);
      context.lineTo(x, height);
      context.stroke();
    }

    // Draw horizontal grid lines
    for (let y = 0; y <= height; y += gridSize) {
      context.beginPath();
      context.moveTo(0, y);
      context.lineTo(width, y);
      context.stroke();
    }
  };

  const drawLine = (
    context: CanvasRenderingContext2D,
    start: Point,
    end: Point,
    color: string = "black"
  ) => {
    context.beginPath();
    context.moveTo(start.x, start.y);
    context.lineTo(end.x, end.y);
    context.strokeStyle = color;
    context.stroke();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const context = canvas.getContext("2d");
      if (context) {
        // Clear the canvas
        context.clearRect(0, 0, width, height);

        // Draw the grid
        drawGrid(context, width, height);

        // Draw the segments
        for (let i = 0; i < points.length - 1; i++) {
          drawLine(context, points[i], points[i + 1], "blue");
        }

        // Draw the bezier curve
        for (let i = 0; i < bezierPoints.length - 1; i++) {
          drawLine(context, bezierPoints[i], bezierPoints[i + 1], "red");
        }
      }
    }
  }, [points, bezierPoints, width, height]); // Redraw when points, bezierPoints, width, or height change

  return <canvas ref={canvasRef} width={width} height={height} />;
};

export default CanvasComponent;
