// CanvasComponent.tsx
import React, { useRef, useEffect } from "react";

type Point = {
  x: number;
  y: number;
};

interface CanvasProps {
  points: Point[];
  bezierPoints: Point[];
}

const CanvasComponent: React.FC<CanvasProps> = ({ points, bezierPoints }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  let width = 0,
    height = 0;
  for (let i = 0; i < points.length; i++) {
    width = Math.max(points[i].x, width);
  }

  const drawLine = (
    context: CanvasRenderingContext2D,
    start: Point,
    end: Point,
    color: string = "black"
  ) => {
    context.beginPath();
    context.moveTo(start.x, start.y);
    context.lineTo(end.x, end.y);
    context.strokeStyle = color;
    context.stroke();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const context = canvas.getContext("2d");
      if (context) {
        // Clear the canvas
        context.clearRect(0, 0, canvas.width, canvas.height);

        // Draw the segments
        for (let i = 0; i < points.length - 1; i++) {
          drawLine(context, points[i], points[i + 1], "blue");
        }

        // Draw the bezier curve
        for (let i = 0; i < bezierPoints.length - 1; i++) {
          drawLine(context, bezierPoints[i], bezierPoints[i + 1], "red");
        }
      }
    }
  }, [points, bezierPoints]); // Redraw when points or bezierPoints change

  return <canvas ref={canvasRef} width={500} height={500} />;
};

export default CanvasComponent;
