import React, { useEffect, useState } from "react";
import "./App.css";
import CanvasComponent from "./components/CanvasComponents";
import Input from "./components/Input";
import { Point } from "./types";
import { QuadraticBezierCurve } from "./logic/Bezier";
import PageContainer from "./components/PageContainer";
import Sidebar from "./components/Sidebar";
import { useLocation } from "react-router-dom";

function App() {
  const [points, setPoints] = useState<Point[]>([]);
  const [bezierPoints, setBezierPoints] = useState<Point[]>([]);

  const location = useLocation();
  const { hash, pathname, search } = location;

  useEffect(() => {
    if (points.length === 3) {
      setBezierPoints(QuadraticBezierCurve(points, 6));
    }
  }, [points]);
  return (
    <div className="App h-[100vh] w-[100vw] bg-sky-950 ">
      <Input setPoints={setPoints} />
      <div className=" flex items-center justify-center">
        <Sidebar />
        <PageContainer />
        {pathname}
      </div>
      <CanvasComponent points={points} bezierPoints={bezierPoints} />
    </div>
  );
}

export default App;
