import React, { useEffect, useState } from "react";
import Input from "./Input";
import { Point } from "../types";
import { QuadraticBezierCurve } from "../logic/Bezier";
import CanvasComponent from "./CanvasComponents";

const PageContainer = () => {
  const [points, setPoints] = useState<Point[]>([]);
  const [bezierPoints, setBezierPoints] = useState<Point[]>([]);

  useEffect(() => {
    if (points.length === 3) {
      setBezierPoints(QuadraticBezierCurve(points, 6));
    }
  }, [points]);

  return (
    <section className="aspect-[16/8.5] p-6 items-center justify-center w-[85%] rounded-b rounded-r bg-cyan-900 shadow-xl z-0 relative">
      <Input setPoints={setPoints} />
      <CanvasComponent points={points} bezierPoints={bezierPoints} />
    </section>
  );
};

export default PageContainer;
