import React, { useEffect, useState } from "react";
import "./App.css";
import CanvasComponent from "./components/CanvasComponents";
import Input from "./components/Input";
import { Point } from "./types";
import { QuadraticBezierCurve } from "./logic/Bezier";

function App() {
  const [points, setPoints] = useState<Point[]>([]);
  const [bezierPoints, setBezierPoints] = useState<Point[]>([]);

  useEffect(() => {
    if (points.length === 3) {
      setBezierPoints(QuadraticBezierCurve(points, 6));
    }
  }, [points]);
  return (
    <div className="App  bg-blue-600">
      <Input setPoints={setPoints} />
      <CanvasComponent points={points} bezierPoints={bezierPoints} />
    </div>
  );
}

export default App;
